CREATE TABLE `cwiki_pages` (
  `page_id` int(9) NOT NULL auto_increment,
  `page_namespace` varchar(30) NOT NULL default '',
  `page_name` varchar(50) NOT NULL default '',
  `page_time` int(11) NOT NULL default '0',
  `page_last_change` int(11) NOT NULL default '0',
  `page_version` varchar(20) NOT NULL default '',
  PRIMARY KEY  (`page_id`),
  UNIQUE KEY `page_name` (`page_namespace`,`page_name`)
) TYPE=MyISAM;

